mapper.select:
SELECT learn_sttus_cd
FROM LEARN_HIST
WHERE learn_hist_no = #{hist_no}

mapper.update:
UPDATE LEARN_HIST
SET    learn_sttus_cd = #{learn_sttus_cd}, issue_task_idx = #{task_idx}, message = #{message}
WHERE  learn_hist_no = #{hist_no}

UPDATE LEARN_HIST
SET    eps = #{eps}
WHERE  learn_hist_no = #{hist_no}

UPDATE LEARN_HIST
SET    ml_result_json = #{result}
WHERE  learn_hist_no = #{hist_no}

UPDATE LEARN_HIST
SET    start_time = #{curr_time}
WHERE  learn_hist_no = #{hist_no}

UPDATE LEARN_HIST
SET    end_time = #{curr_time}
WHERE  learn_hist_no = #{hist_no}
