mapper.insert:
INSERT INTO PROJECT_INFO
(	project_id
,	project_purpose
,	dataset_id
,   status
,   start_time
)
VALUES
    (	#{project_id}
    ,	#{project_purpose_cd}
    ,	#{dataset_id}
    ,	#{status}
    ,	#{start_time}
    )

mapper.select:
SELECT learn_hist_no, status
FROM LEARN_HIST
WHERE project_id = #{project_id}

mapper.update:
UPDATE PROJECT_INFO
SET    status = #{status}
WHERE  project_id = #{project_id}
